import React, {useState,useEffect} from 'react'
import 'codemirror/lib/codemirror.css'
import 'codemirror/theme/material.css'
import 'codemirror/mode/javascript/javascript' 
import {Controlled as ControlledEditor} from 'react-codemirror2'
import './Editor.css'
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom';
const socket = io.connect("https://localhost:3001")

export default function Editor(props) {
    const {language, projectName,code, onChange} = props;
    const [userCount, setUserCount] = useState(0);
    const location = useLocation();

    function handleChange(editor,data,code){
    onChange(code);
    }
    
    //handle socket
    useEffect(() => {
        const room = location.pathname; // Use pathname as room identifier
    
        socket.emit('joinRoom', room);
    
        // Handle incoming user count updates
        socket.on('userCount', (count) => {
          setUserCount(count);
        });
    
        // Clean up the subscription on component unmount
        return () => {
          socket.off('userCount');
        };
      }, [location.pathname]);

      //create component
    return (
    <div className="editor-container">
        <div className="project-name-title">
            <div>{projectName} </div>
            <div>number of active users {userCount} </div>
        </div>

    <ControlledEditor 
    onBeforeChange={handleChange}
    value = {code}
    className='code-mirror-wrapper'
    options={{
        lineNumbers: true,
        lineWrapping :true,
        lint: true,
        theme: 'material',
        mode: language
    }}
    />
    </div>
  )
}
